package com.example.moviesapp.views;

import android.content.Intent;
import android.content.SharedPreferences;
import android.os.Bundle;
import android.preference.PreferenceManager;
import android.util.Log;
import android.view.Menu;
import android.view.MenuInflater;
import android.view.MenuItem;
import android.view.View;
import android.widget.TextView;
import android.widget.Toast;

import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.appcompat.app.AppCompatActivity;
import androidx.appcompat.widget.Toolbar;
import androidx.recyclerview.widget.ItemTouchHelper;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import com.example.moviesapp.R;
import com.example.moviesapp.interfaces.IList;
import com.example.moviesapp.models.EntityFilm;
import com.example.moviesapp.presenters.ListPresenter;
import com.google.android.material.floatingactionbutton.FloatingActionButton;

import java.util.ArrayList;

public class ListActivity extends AppCompatActivity implements IList.View {

    private static final String TAG = "/views/ListActivity";
    private IList.Presenter presenter;
    private ArrayList<EntityFilm> items = new ArrayList<>();
    private FilmAdapter adapter;
    private TextView results;
    private Boolean flag = false;
    private RecyclerView recyclerView;
    private String title, genre, date;


    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_list);
        presenter = new ListPresenter(this);
        Toolbar toolbar = findViewById(R.id.toolbar);
        setSupportActionBar(toolbar);
        getSupportActionBar().setTitle(R.string.list_filmsTitle);
        recyclerView = (RecyclerView) findViewById(R.id.recyclerViewList);
        results = findViewById(R.id.num_result);

        SharedPreferences myPreferences
                = PreferenceManager.getDefaultSharedPreferences(ListActivity.this);
        SharedPreferences.Editor myEditor = myPreferences.edit();

        items = presenter.getAllItemsSummarize();

        if (items.size() == 0 && !flag) {
            myEditor.putBoolean("FIRST?", true);
            myEditor.apply();
            flag = true;
        }

        boolean firstTime = myPreferences.getBoolean("FIRST?", false);

        if (firstTime) {
            Log.d(TAG, "Insertando Datos por defecto...");
            EntityFilm film = new EntityFilm();
            film.setId("1");
            film.setDate("30/11/2001");
            film.setTitle("Harry Potter y la piedra filosofal");
            film.setGenre("Fantasy");
            film.setPhoto("");
            presenter.insertOnBBDD(film);
            film.setId("2");
            film.setDate("29/11/2002");
            film.setTitle("Harry potter y la camara secreta");
            film.setGenre("Fantasy");
            film.setPhoto("");
            presenter.insertOnBBDD(film);
            film.setId("3");
            film.setDate("4/6/2004");
            film.setTitle("Harry potter y el prisionero de azkaban");
            film.setGenre("Fantasy");
            film.setPhoto("");
            presenter.insertOnBBDD(film);
            film.setId("4");
            film.setDate("30/9/1988");
            film.setTitle("La jungla de cristal");
            film.setGenre("Action");
            film.setPhoto(null);
            presenter.insertOnBBDD(film);
            film.setId("5");
            film.setDate("20/7/2012");
            film.setTitle("El caballero oscuro: La leyenda renace");
            film.setPhoto(null);
            presenter.insertOnBBDD(film);
            film.setId("6");
            film.setDate("19/2/2010");
            film.setTitle("Shutter island");
            film.setPhoto(null);
            presenter.insertOnBBDD(film);
            film.setId("7");
            film.setDate("25/1/2020");
            film.setTitle("Aves de presa");
            film.setPhoto(null);
            presenter.insertOnBBDD(film);
            film.setId("8");
            film.setDate("21/8/2014");
            film.setTitle("Guten Tag, Ramón");
            film.setPhoto(null);
            presenter.insertOnBBDD(film);
            film.setId("9");
            film.setDate("5/1/2018");
            film.setTitle("Que baje dios y lo vea");
            film.setPhoto(null);
            presenter.insertOnBBDD(film);
            film.setId("10");
            film.setDate("16/3/2001");
            film.setTitle("Enemigo a las puertas");
            film.setPhoto(null);
            presenter.insertOnBBDD(film);

            myEditor.putBoolean("FIRST?", false);
            myEditor.apply();

        } else {
            Log.d(TAG, "The DDBB exist so the example data isn't charge");
        }

        ItemTouchHelper.SimpleCallback simpleCallback = new ItemTouchHelper.SimpleCallback(0, ItemTouchHelper.RIGHT) {
            @Override
            public boolean onMove(@NonNull RecyclerView recyclerView, @NonNull RecyclerView.ViewHolder viewHolder, @NonNull RecyclerView.ViewHolder target) {
                return false;
            }

            @Override
            public void onSwiped(@NonNull final RecyclerView.ViewHolder viewHolder, int direction) {
                final int position = viewHolder.getAdapterPosition();
                switch (direction) {
                    case ItemTouchHelper.RIGHT:
                        presenter.onSwipe(position);
                        presenter.showToast();
                        break;
                }

            }

        };
        ItemTouchHelper itemTouchHelper = new ItemTouchHelper(simpleCallback);
        itemTouchHelper.attachToRecyclerView(recyclerView);

        FloatingActionButton fab = findViewById(R.id.fab);
        fab.setOnClickListener(v -> {
            presenter.onClickAddMovie();
        });

    }

    public void onItemSwipe(int position) {
        EntityFilm delete = items.get(position);
        if (presenter.deleteFilm(delete)) {
            Log.d(TAG, "Item removed");
            adapter.notifyItemRemoved(position);
            results.setText(adapter.getItemCount() + getString(R.string.result));
        } else {
            Log.d(TAG, "Item not removed");
        }
    }

    @Override
    public void showDeleteToast() {
        Toast.makeText(MyApplication.getContext(), "Borrado con éxito", Toast.LENGTH_LONG).show();
    }

    @Override
    public boolean onCreateOptionsMenu(Menu menu) {
        MenuInflater inflater = getMenuInflater();
        inflater.inflate(R.menu.main_menu, menu);
        return true;
    }

    @Override
    public boolean onOptionsItemSelected(@NonNull MenuItem item) {
        switch (item.getItemId()){
            case R.id.search:
                presenter.onClickSearchButton();
                break;
            case R.id.aboutApp:
                presenter.onClickAPPCRUD();
                break;
        }
        return super.onOptionsItemSelected(item);
    }

    @Override
    protected void onRestart() {
        Log.d(TAG, "Inside onRestart()");
        super.onRestart();
    }

    @Override
    protected void onStop() {
        Log.d(TAG, "Inside onStop()");
        super.onStop();
    }

    @Override
    protected void onResume() {
        Log.d(TAG, "Inside onResume()");
        super.onResume();

        if (genre != null && date == null && title == null) {
            items.clear();
            items = presenter.getItemsByCriterion("genre", genre);
            System.out.println("item" + items);
        } else if (date != null && genre == null && title == null) {
            items.clear();
            items = presenter.getItemsByCriterion("date", date);
        } else if (title != null && genre == null && date == null) {
            items.clear();
            items = presenter.getItemsByCriterion("title", title);
        } else if (title != null && genre != null && date != null) {
            items.clear();
            items = presenter.getItemsByAllCriterions(genre, date, title);
        } else if (date == null && title == null && genre == null) {
            items.clear();
            items = presenter.getAllItemsSummarize();
        }

        adapter = new FilmAdapter(items, getApplicationContext());

        results = findViewById(R.id.num_result);
        results.setText(adapter.getItemCount() + getString(R.string.result));

        recyclerView.setAdapter(adapter);

        recyclerView.setLayoutManager(new LinearLayoutManager(this));

        // Asocia el elemento de la lista con una acción al ser pulsado
        adapter.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                // Acción al pulsar el elemento
                int position = recyclerView.getChildAdapterPosition(v);
                presenter.onClickRecyclerViewItem(items.get(position).getId());
            }
        });

    }

    @Override
    protected void onPause() {
        Log.d(TAG, "Inside onPause()");
        super.onPause();
    }

    @Override
    protected void onDestroy() {
        Log.d(TAG, "Inside onDestroy()");
        super.onDestroy();
    }

    @Override
    protected void onStart() {
        Log.d(TAG, "Inside onStart()");
        super.onStart();
    }

    @Override
    public void startFormActivity() {
        Log.d(TAG, "Inside startFormActivity()");
        Intent intent = new Intent(getApplicationContext(), FormActivity.class);
        startActivity(intent);
    }

    @Override
    public void startFormActivity(String id) {
        Log.d(TAG, "Inside startFormActivity with id");
        Intent intent = new Intent(getApplicationContext(), FormActivity.class);
        intent.putExtra("id",id);
        startActivity(intent);
    }

    public void startSearchActivity() {
        Log.d(TAG, "Inside startSearchActivity()");
        Intent intent = new Intent(getApplicationContext(), SearchActivity.class);
        startActivityForResult(intent, 1);
    }

    public void startAppCRUDActivity() {
        Log.d(TAG, "Inside startAppCRUDActivity");
        Intent intent = new Intent(getApplicationContext(), AppCRUDActivity.class);
        startActivity(intent);
    }

    @Override
    protected void onActivityResult(int requestCode, int resultCode, @Nullable Intent data) {
        super.onActivityResult(requestCode, resultCode, data);
        if (requestCode == 1) {
            if (resultCode == 1) {
                title = data.getStringExtra("title");
                genre = data.getStringExtra("genre");
                date = data.getStringExtra("date");
            }
        }
    }
}